(31, 'habit-moment-survey', 'marketing', 'en_US', 'We\'re all ears! Share your experience so far with WooCommerce', 'We\'d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2021-11-21 08:32:33', NULL, 0, 'plain', '', 0, 'info'),
(32, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2021-11-21 08:32:52', NULL, 0, 'plain', '', 0, 'info'),
(33, 'surface_cart_checkout', 'info', 'en_US', 'Introducing the Cart and Checkout blocks!', 'Increase your store\'s revenue with the conversion optimized Cart &amp; Checkout WooCommerce blocks available in the WooCommerce Blocks extension.', '{}', 'unactioned', 'woo-gutenberg-products-block', '2021-11-21 08:32:52', NULL, 0, 'plain', '', 0, 'info'),
